//
// State
const initAppState = {
	system: 0,
	game: 0
};

function getSystem(id) {
	return _.filter(all_systems, (sys) => sys.id == id);
}

function getGame(id) {
	return _.filter(all_games, (game) => game.id == id);
}

//
// Actions

function selectSystem(id) {
	return {
		type: 'SELECT_SYSTEM',
		id: id
	};
}

function selectGame(id) {
	return {
		type: 'SELECT_GAME',
		id: id
	};
}

function unselectGame() {
	return {
		type: 'RESET_GAME'
	};
}

function unselectSysteM() {
	return {
		type: 'RESET_SYSTEM'
	};
}

//
// Reducers

function updateState(state, action) {
	switch (action.type) {
	case 'SELECT_GAME':
		return _.assign({}, state, {game: action.id});
		break;
	case 'SELECT_SYSTEM':
		return _.assign({}, state, {system: action.id});
		break;
	case 'RESET_GAME':
		return _.assign({}, state, {game: 0});
		break;
	case 'RESET_SYSTEM':
		return initAppState;
		break;
	default:
		return state;
	}
};

//
// Components

const GameView = (props) => {
	return (<div className="gameView"><h3>{props.name}</h3><p>{props.comments}</p><p>{props.date}</p></div>);
};

GameView.defaultProps = {
	name: 'Default Name',
	comments: 'Default Comments'
};

const SystemView = (props) => {
	console.log("system props", props);
	return (<div className="systemView"><h2>{props.company} - {props.name}</h2><p>{props.comments}</p></div>);
};

SystemView.defaultProps = {
	company: 'ZZZ',
	name: 'Default Name',
	comments: 'Default Comments'
};

const AppView = (props) => {
	const systemItems = props.systems.map((sys) => {
		return (<li><SystemView {...sys} key={"system_" + sys.id} /></li>);
	});
	const emptySystems = <p>No system right now</p>;
	return (<div className="appView"><h1>Nicks Games</h1><ol>{systemItems}</ol></div>);
};

//
// Containers

const mapStateToProps = (state) => {
	return {
		systems: all_systems,
		selectedSystem: state.system
	};
};

const mapDispatchToProps = (dispatch) => {
	return {};
}

const AppContainer = ReactRedux.connect(
	mapStateToProps,
	mapDispatchToProps
)(AppView);

//
// Put it together

const store = Redux.createStore(updateState, initAppState);

ReactDOM.render(
    <ReactRedux.Provider store={store}>
		<AppContainer />
	</ReactRedux.Provider>,
    document.getElementById('app-root')
);
